{/*importing elements from bootstrap*/}
import { Navbar, Container, Nav } from "react-bootstrap"
{/*importing useState for managing information*/}
import { useState, useEffect } from "react"
{/*importing assets*/}
import logo from '../assets/img/logoTransparent.svg'
import navBar1 from '../assets/img/search.svg'
import navBar2 from '../assets/img/facebook-logo.svg'
import navBar3 from '../assets/img/whatsapp.svg'

{/*creating navbar component*/}
export const NavBar = () => {

    {/*managing active links, and setting the initialy active one home*/}
    const [activeLink, setActiveLink] = useState('home')
    {/*making scroll animations, to know when the user is scrolling to another section, so animations happen*/}
    const [scrolled, setScrolled] = useState(false)
    {/*triggers when a user scrolls*/}
    useEffect(() => {
        const onScroll = () => {
            if (window.scrollY > 50) {
                setScrolled(true)
            } else {
                setScrolled(false)
            }
        }
        {/*adding a listener for the function*/}
        window.addEventListener('scroll', onScroll)
        {/*removing the listener when the event happens*/}
        return () => window.removeEventListener('scroll', onScroll)
    }, [])

    {/*makes the link the user has clicked on active*/}
    const onUpdateActiveLink = (value) => {
        setActiveLink(value)
    }

    return (
        <Navbar expand="lg" className={ scrolled ? "scrolled" : ""}> 
        {/*if it is scrolled, then give it a class name scrolled, and if not, do not give it a name*/}
        <Container>
          <Navbar.Brand href="#home">
            <img src = {logo} alt="Logo" /> 
          </Navbar.Brand>
          {/*for mobile devices when image is minimized*/}
          <Navbar.Toggle aria-controls="basic-navbar-nav">
            <span className="navbar-toggler-icon"></span>
          </Navbar.Toggle>
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="me-auto">
              <Nav.Link href="#home" className={activeLink === 'home' ? 'active navbar-link' : 'navbar-link'} onClick={() => onUpdateActiveLink('home')}>Home</Nav.Link> 
              {/*if the active link is home, then give it a className*/}
              <Nav.Link href="#skills" className={activeLink === 'skilss' ? 'active navbar-link' : 'navbar-link'} onClick={() => onUpdateActiveLink('skills')}>My Skills</Nav.Link>
              <Nav.Link href="#projects" className={activeLink === 'projects' ? 'active navbar-link' : 'navbar-link'} onClick={() => onUpdateActiveLink('projects')}>My Projects</Nav.Link>
            </Nav>
            <span className="navbar-text">
                <div className="social-icon">
                    <a href="#"><img src={navBar1} alt=""></img></a>
                    <a href="#"><img src={navBar2} alt=""></img></a>
                    <a href="#"><img src={navBar3} alt=""></img></a>
                </div>
                <button className="vvd" onClick={() => console.log('hey')}><span>Contact Me!</span></button>
            </span>
          </Navbar.Collapse>
        </Container>
      </Navbar>
    )
}
