import { useState } from "react"
import { Col, Container, Row } from "react-bootstrap"
import contactPic from '../assets/img/smile.gif'

const Contact = () => {

    const formInitialDetails = {
        firstName: '',
        lastName: '',
        email: '',
        phone: '',
        message: ''
    }

    const [formDetails, setFormDetails] = useState(formInitialDetails)
    const [buttonText, setButtonText] = useState('Send')
    const [status, setStatus] = useState({})

   const onFormUpdate = (category, value) => {
    setFormDetails({
        ...formDetails,
        [category]:value
    })
   }

   const handleSubmit = async(e) => {
        e.preventDefault()
        setButtonText('Sending...')
        console.log("helloooo")
        let response = await ax("http://localhost:3000/contact", {
            method:"POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(formDetails),
        });

        setButtonText('Send')
        let result = response.json()
        setFormDetails(formInitialDetails)
        if (result.code === 200) {
            setStatus({success: true, message:'Message Sent Successfully'})
        }else{
            setStatus({success: false, message:'Something went wrong'})
        }
   }

    return (
        <section className="contact" id="connect">
            <Container>
                <Row className="align-items-center">
                    <Col md={6}>
                        <img src={contactPic} className="ContactImg" alt="contImg"/>
                    </Col>
                    <Col md={6}>
                        <h2>Get In Touch</h2>
                        <h3>&#40;with me &#40;: &#41;</h3>
                        <form onSubmit={handleSubmit}>
                            <Row>
                                <Col sm={6} className="px-1">
                                    <input type="text" value={formDetails.firstName} placeholder="First Name" onChange={(e) => onFormUpdate('firstName', e.target.value)}/>
                                </Col>
                                <Col sm={6} className="px-1">
                                <input type="text" value={formDetails.lastName} placeholder="Last Name" onChange={(e) => onFormUpdate('lastName', e.target.value)}/>
                                </Col>
                                <Col sm={6} className="px-1">
                                <input type="email" value={formDetails.email} placeholder="Your Email" onChange={(e) => onFormUpdate('email', e.target.value)}/>
                                </Col>
                                <Col sm={6} className="px-1">
                                <input type="tel" value={formDetails.phone} placeholder="Your Phone" onChange={(e) => onFormUpdate('phone', e.target.value)}/>
                                </Col>
                                <Col>
                                <input type="textarea" row="6" value={formDetails.message} placeholder="Your Message" onChange={(e) => onFormUpdate('message', e.target.value)}></input>
                                <button type="submit"><span>{buttonText}</span></button>
                                </Col>
                                {
                                    status.message && 
                                    <Col>
                                        <p className={status.success === false ? "danger" : "success"}>{status.message}</p>
                                    </Col>
                                }
                            </Row>
                        </form>
                    </Col>

                </Row>
            </Container>
        </section>
    )
}

export default Contact
