import { useEffect, useState } from "react";
import { Col, Container, Row } from "react-bootstrap";
import { ArrowRightCircle } from "react-bootstrap-icons";
// import { moonTransparent } from "../assets/img/moon.svg"
import { Moon } from ""


const Banner = () => {

    const [ loopNum, setLoopNum ] = useState(0)
    const [ isDeleting, setIsDeleting] = useState(false)
    const [ text, setText ] = useState('')
    const [ delta, setDelta ] = useState(300 - Math.random()*100)
    const period = 2000
    const rotation = [ "Tsveta Pandurska", "Fontys Passionate student", "Aspiring WebDeveloper"]

    useEffect(() => {
        let ticker = setInterval(() => {
            tick()
        }, delta);

        return () => {
            clearInterval(ticker)
        }
    }, [text])

    const tick = () => {
        let i = loopNum % rotation.length
        let fullText = rotation[i]
        let updatedText = isDeleting ? fullText.substring(0, text.length - 1) : fullText.substring(0, text.length + 1)
        setText(updatedText)

        if (isDeleting) {
            setDelta(prevDelta => prevDelta/2)
        }

        if (!isDeleting && updatedText === fullText) {
            setIsDeleting(true)
            setDelta(period)
        }else if (isDeleting && updatedText === '') {
            setIsDeleting(false)
            setLoopNum(loopNum + 1)
            setDelta(500)
        }
            
        
    }

    return (
        <section className="banner" id="home">
            <Container>
                <Row  className="align-items-center">
                    <Col xs={12} md={6} xl={7}>
                        <h1>{`Welcome! I am`}<span className="wrap">{text}</span></h1>
                        <p>Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. </p>
                        <button onClick={() => console.log('connect')}>Let's Talk!<ArrowRightCircle size={24}/></button>
                    </Col>
                    <Col xs={12} md={6} xl={5}>
                        <img src={moonTransparent} alt="image" />
                    </Col>
                </Row>
            </Container>
        </section>
    )
    
}

export default Banner
