import { Navbar, Container, Nav } from "react-bootstrap"
import { useState, useEffect } from "react"
import logo from '../assets/img/logoTransparent.svg'
import navBar1 from '../assets/img/search.svg'
import navBar2 from '../assets/img/facebook-logo.svg'
import navBar3 from '../assets/img/whatsapp.svg'


export const NavBar = () => {

    const [activeLink, setActiveLink] = useState('home')
    const [scrolled, setScrolled] = useState(false)
    
    useEffect(() => {
        const onScroll = () => {
            if (window.scrollY > 50) {
                setScrolled(true)
            } else {
                setScrolled(false)
            }
        }
        window.addEventListener('scroll', onScroll)
        return () => window.removeEventListener('scroll', onScroll)
    }, [])

    const onUpdateActiveLink = (value) => {
        setActiveLink(value)
    }

    return (
        <Navbar expand="lg" className={ scrolled ? "scrolled" : ""}> 
        <Container>
          <Navbar.Brand href="#home">
            <img src = {logo} alt="Logo" /> 
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav">
            <span className="navbar-toggler-icon"></span>
          </Navbar.Toggle>
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="me-auto">
              <Nav.Link href="#home" className={activeLink === 'home' ? 'active navbar-link' : 'navbar-link'} onClick={() => onUpdateActiveLink('home')}>Home</Nav.Link> 
              <Nav.Link href="#skills" className={activeLink === 'skilss' ? 'active navbar-link' : 'navbar-link'} onClick={() => onUpdateActiveLink('skills')}>My Skills</Nav.Link>
              <Nav.Link href="#projects" className={activeLink === 'projects' ? 'active navbar-link' : 'navbar-link'} onClick={() => onUpdateActiveLink('projects')}>My Projects</Nav.Link>
            </Nav>
            <span className="navbar-text">
                <div className="social-icon">
                    <a href="#"><img src={navBar1} alt=""></img></a>
                    <a href="#"><img src={navBar2} alt=""></img></a>
                    <a href="#"><img src={navBar3} alt=""></img></a>
                </div>
                <button className="vvd" onClick={() => console.log('hey')}><span>Contact Me!</span></button>
            </span>
          </Navbar.Collapse>
        </Container>
      </Navbar>
    )
}
