import { Col, Container, Nav, Row, Tab, TabContent, TabPane } from "react-bootstrap"
import { ProjectCard } from "./ProjectCard"

const Projects = () => {

    const projectsProg = [
        {
            title: "Use of Api",
            description: "1",
            // imgUrl: single100suffix.png,
        },
        {
            title: "Use of Local Storage",
            description: "2",
            // imgUrl: progImg2,
        },
        {
            title: "Use of Node.js and MongoDB",
            description: "3",
            // imgUrl: progImg3,
        },
    ]

    const projectsMedia = [
        {
            title: "bla",
            description: "bla",
            // imgUrl: single100suffix.png,
        },
        {
            title: "blaa",
            description: "blaa",
            // imgUrl: progImg2,
        },
        {
            title: "hihi",
            description: "hihi",
            // imgUrl: progImg3,
        },]

    return (
        <section className="project" id="project">
            <Container>
                <Row>
                    <Col>
                        <h2>My Projects</h2>
                        <p>Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum is simply dummy text of the printing and typesetting industry. </p>
                        <Tab.Container id="projects-tabs" defaultActiveKey='first'>
                        <Nav variant="pills" defaultActiveKey="/home">
                            <Nav.Item>
                                <Nav.Link eventKey='first'>Web Development</Nav.Link>
                            </Nav.Item>
                            <Nav.Item>
                                <Nav.Link eventKey='second'>UX Design</Nav.Link>
                            </Nav.Item>
                            <Nav.Item>
                                <Nav.Link eventKey='third' >Media Production</Nav.Link>
                            </Nav.Item>
                        </Nav>
                        <TabContent>
                            <TabPane eventKey='first'>
                                <Row>
                                    {
                                        projectsProg.map((project, index) => {
                                            return (
                                                <ProjectCard 
                                                  key={index}
                                                   {...project}
                                                   />
                                            )
                                        })
                                    }
                                </Row>
                            </TabPane>
                            <TabPane eventKey='second'>
                            <Row>
                                    {
                                        projectsMedia.map((project, index) => {
                                            return (
                                                <p>{project.title}</p>
                                            )
                                        })
                                    }
                                </Row>
                            </TabPane>
                            <TabPane eventKey='third'>Lorem Ipsum</TabPane>
                        </TabContent>
                        </Tab.Container>
                    </Col>
                </Row>
            </Container>
        </section>
    )
}

export default Projects
