import { Col, Container, Nav, Row, Tab, TabContent, TabPane } from "react-bootstrap"
import { ProjectCard } from "./ProjectCard"
import apiimg from '../assets/img/api-challenge.png'
import booklistpic from '../assets/img/booklist-challenge.png'
import dbpic from '../assets/img/database-challenge.png'

const Projects = () => {

    // pre-defining arrays of objects, in order to display 
    const projectsProg = [
        {
            title: "Use of Api",
            description: "1",
            imgUrl: apiimg,
        },
        {
            title: "Use of Local Storage",
            description: "2",
            imgUrl: booklistpic,
        },
        {
            title: "Use of Node.js and MongoDB",
            description: "3",
            imgUrl: dbpic,
        },
    ]

    const projectsMedia = [
        {
            title: "bla",
            description: "bla",
            // imgUrl: single100suffix.png,
        },
        {
            title: "blaa",
            description: "blaa",
            // imgUrl: progImg2,
        },
        {
            title: "hihi",
            description: "hihi",
            // imgUrl: progImg3,
        },]

        const projectsUX = [
            {
                title: "bla",
                description: "bla",
                // imgUrl: single100suffix.png,
            },
            {
                title: "blaa",
                description: "blaa",
                // imgUrl: progImg2,
            },
            {
                title: "hihi",
                description: "hihi",
                // imgUrl: progImg3,
            },]

    return (
        <section className="project" id="projects">
            <Container>
                <Row>
                    <Col>
                        <h2>My Projects</h2>
                        <p>Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum is simply dummy text of the printing and typesetting industry. </p>
                        <Tab.Container id="projects-tabs" defaultActiveKey='first'>
                        <Nav variant="pills" id="pills-tab">
                            <Nav.Item>
                                <Nav.Link eventKey='first'>Web Development</Nav.Link>
                            </Nav.Item>
                            <Nav.Item>
                                <Nav.Link eventKey='second'>UX Design</Nav.Link>
                            </Nav.Item>
                            <Nav.Item>
                                <Nav.Link eventKey='third' >Media Production</Nav.Link>
                            </Nav.Item>
                        </Nav>
                        <TabContent>
                            <TabPane eventKey='first'>
                                <Row>
                                    {
                                        projectsProg.map((project, index) => {
                                            return (
                                                <ProjectCard 
                                                  key={index}
                                                   {...project}
                                                   />
                                            )
                                        })
                                    }
                                </Row>
                            </TabPane>
                            <TabPane eventKey='second'>
                            <Row>
                                    {
                                        projectsUX.map((project, index) => {
                                            return (
                                                <ProjectCard 
                                                  key={index}
                                                   {...project}
                                                   />
                                            )
                                        })
                                    }
                                </Row>
                            </TabPane>
                            <TabPane eventKey='third'> <Row>
                                    {
                                        projectsMedia.map((project, index) => {
                                            return (
                                                <ProjectCard 
                                                  key={index}
                                                   {...project}
                                                   />
                                            )
                                        })
                                    }
                                </Row></TabPane>
                        </TabContent>
                        </Tab.Container>
                    </Col>
                </Row>
            </Container>
        </section>
    )
}

export default Projects
